import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { sampleMovies } from '../sampleMovies';

const AddReview = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const movie = sampleMovies.find(m => m.id === parseInt(id));

  const [review, setReview] = useState({
    rating: 5,
    comment: '',
    reviewerName: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // In a real app, this would save to a database
    console.log('Review submitted:', { movieId: id, ...review });
    alert('Review submitted successfully!');
    navigate(`/movie/${id}`);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setReview(prev => ({
      ...prev,
      [name]: value
    }));
  };

  if (!movie) {
    return <div>Movie not found</div>;
  }

  return (
    <div className="add-review">
      <h1>Add Review for {movie.title}</h1>

      <div className="row">
        <div className="col-md-4">
          <img src={movie.poster} className="img-fluid rounded" alt={movie.title} />
        </div>
        <div className="col-md-8">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="reviewerName" className="form-label">Your Name</label>
              <input
                type="text"
                className="form-control"
                id="reviewerName"
                name="reviewerName"
                value={review.reviewerName}
                onChange={handleChange}
                required
              />
            </div>

            <div className="mb-3">
              <label htmlFor="rating" className="form-label">Rating</label>
              <select
                className="form-select"
                id="rating"
                name="rating"
                value={review.rating}
                onChange={handleChange}
              >
                <option value="1">1 Star</option>
                <option value="2">2 Stars</option>
                <option value="3">3 Stars</option>
                <option value="4">4 Stars</option>
                <option value="5">5 Stars</option>
              </select>
            </div>

            <div className="mb-3">
              <label htmlFor="comment" className="form-label">Review Comment</label>
              <textarea
                className="form-control"
                id="comment"
                name="comment"
                rows="5"
                value={review.comment}
                onChange={handleChange}
                placeholder="Share your thoughts about this movie..."
                required
              ></textarea>
            </div>

            <button type="submit" className="btn btn-primary me-2">Submit Review</button>
            <button type="button" className="btn btn-secondary" onClick={() => navigate(`/movie/${id}`)}>
              Cancel
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddReview;
