import React from 'react';
import { Link } from 'react-router-dom';
import { sampleMovies } from '../sampleMovies';

const MyReviews = () => {
  // Mock user's reviews - in a real app, this would come from an API
  const myReviews = [
    {
      id: 1,
      movieId: 1,
      movieTitle: 'The Shawshank Redemption',
      rating: 5,
      comment: 'An absolute masterpiece. The story and performances are incredible.',
      date: '2023-11-15'
    },
    {
      id: 2,
      movieId: 2,
      movieTitle: 'The Godfather',
      rating: 4,
      comment: 'Classic film with great acting. A bit long but worth it.',
      date: '2023-11-12'
    }
  ];

  const getMoviePoster = (movieId) => {
    const movie = sampleMovies.find(m => m.id === movieId);
    return movie ? movie.poster : '';
  };

  return (
    <div className="my-reviews">
      <h1>My Reviews</h1>

      {myReviews.length > 0 ? (
        <div className="reviews-list">
          {myReviews.map(review => (
            <div key={review.id} className="card mb-3">
              <div className="row g-0">
                <div className="col-md-3">
                  <img
                    src={getMoviePoster(review.movieId)}
                    className="img-fluid rounded-start h-100"
                    alt={review.movieTitle}
                    style={{ objectFit: 'cover' }}
                  />
                </div>
                <div className="col-md-9">
                  <div className="card-body">
                    <div className="d-flex justify-content-between align-items-start mb-2">
                      <h5 className="card-title">
                        <Link to={`/movie/${review.movieId}`} className="text-decoration-none">
                          {review.movieTitle}
                        </Link>
                      </h5>
                      <div className="d-flex align-items-center">
                        <span className="badge bg-warning text-dark me-2">
                          {'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}
                        </span>
                        <small className="text-muted">{review.date}</small>
                      </div>
                    </div>
                    <p className="card-text">{review.comment}</p>
                    <div className="review-actions">
                      <Link to={`/update-review/${review.id}`} className="btn btn-outline-primary btn-sm me-2">
                        Edit
                      </Link>
                      <button className="btn btn-outline-danger btn-sm">Delete</button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="text-center py-5">
          <p className="text-muted">You haven't written any reviews yet.</p>
          <Link to="/browse" className="btn btn-primary">Browse Movies</Link>
        </div>
      )}
    </div>
  );
};

export default MyReviews;
